#!/bin/bashn# create-version.sh - Create new versioned template for significant changesnnecho "💾 Creating new versioned template..."nn# Check if language parameter is providednif [ -z "$1" ]; thenn    echo "❌ ERROR: Language parameter required"n    echo "Usage: ./create-version.sh <language> [version-description]"n    exit 1nfinnLANGUAGE="$1"nDESCRIPTION="${2:-$(date +%Y%m%d)}"nnecho "   Language: $LANGUAGE"necho "   Description: $DESCRIPTION"nn# Check if updated template existsnif [ ! -f "templates/prp-base-updated.md" ]; thenn    echo "❌ ERROR: Updated PRP template not found!"n    echo "   Run update-prp-template.sh first"n    exit 1nfinn# Create versioned templates directorynmkdir -p "templates/versioned-templates"nn# Find next version numbernVERSION=1nwhile [ -f "templates/versioned-templates/$LANGUAGE-template-v$VERSION.md" ]; don    VERSION=$((VERSION + 1))ndonn# Create versioned templatencp "templates/prp-base-updated.md" "templates/versioned-templates/$LANGUAGE-template-v$VERSION.md"nnecho "   ✅ Version $VERSION created: $LANGUAGE-template-v$VERSION.md"necho "   📝 Description: $DESCRIPTION"nnecho "   💾 Location: templates/versioned-templates/"nn# Create version metadatanecho "   📊 Creating version metadata..."ncat > "templates/versioned-templates/$LANGUAGE-template-v$VERSION.meta.json" << EOFn{n  "language": "$LANGUAGE",n  "version": $VERSION,n  "created_date": "$(date -I)",n  "description": "$DESCRIPTION",n  "source_template": "prp-base-updated.md",n  "changes": [n    "Template updated for $LANGUAGE",n    "Language-specific patterns added",n    "Version $VERSION created"n  ]n}nEOFnnecho "   ✅ Version metadata created"necho ""necho "🎉 Version $VERSION template created successfully!"necho "📄 Template: $LANGUAGE-template-v$VERSION.md"necho "📊 Metadata: $LANGUAGE-template-v$VERSION.meta.json"n