#!/bin/bashn# create-version.sh - Create new versioned template for significant changesnnecho "ğŸ’¾ Creating new versioned template..."nn# Check if language parameter is providednif [ -z "$1" ]; thenn    echo "âŒ ERROR: Language parameter required"n    echo "Usage: ./create-version.sh <language> [version-description]"n    exit 1nfinnLANGUAGE="$1"nDESCRIPTION="${2:-$(date +%Y%m%d)}"nnecho "   Language: $LANGUAGE"necho "   Description: $DESCRIPTION"nn# Check if updated template existsnif [ ! -f "templates/prp-base-updated.md" ]; thenn    echo "âŒ ERROR: Updated PRP template not found!"n    echo "   Run update-prp-template.sh first"n    exit 1nfinn# Create versioned templates directorynmkdir -p "templates/versioned-templates"nn# Find next version numbernVERSION=1nwhile [ -f "templates/versioned-templates/$LANGUAGE-template-v$VERSION.md" ]; don    VERSION=$((VERSION + 1))ndonn# Create versioned templatencp "templates/prp-base-updated.md" "templates/versioned-templates/$LANGUAGE-template-v$VERSION.md"nnecho "   âœ… Version $VERSION created: $LANGUAGE-template-v$VERSION.md"necho "   ğŸ“ Description: $DESCRIPTION"nnecho "   ğŸ’¾ Location: templates/versioned-templates/"nn# Create version metadatanecho "   ğŸ“Š Creating version metadata..."ncat > "templates/versioned-templates/$LANGUAGE-template-v$VERSION.meta.json" << EOFn{n  "language": "$LANGUAGE",n  "version": $VERSION,n  "created_date": "$(date -I)",n  "description": "$DESCRIPTION",n  "source_template": "prp-base-updated.md",n  "changes": [n    "Template updated for $LANGUAGE",n    "Language-specific patterns added",n    "Version $VERSION created"n  ]n}nEOFnnecho "   âœ… Version metadata created"necho ""necho "ğŸ‰ Version $VERSION template created successfully!"necho "ğŸ“„ Template: $LANGUAGE-template-v$VERSION.md"necho "ğŸ“Š Metadata: $LANGUAGE-template-v$VERSION.meta.json"n