#!/bin/bashn# detect-language.sh - Auto-detect project language from files and contextnnecho "üîç Detecting project language..."nn# Check for common language indicatorsnif [ -f "package.json" ]; thenn    echo "   Found package.json - JavaScript/Node.js project"n    if grep -q "next" package.json; thenn        echo "   ‚úÖ Language: Next.js (JavaScript/TypeScript)"n        echo "NEXTJS"n        exit 0n    elif grep -q "react" package.json; thenn        echo "   ‚úÖ Language: React (JavaScript/TypeScript)"n        echo "REACT"n        exit 0n    else n        echo "   ‚úÖ Language: Node.js (JavaScript)"n        echo "NODEJS"n        exit 0n    finnfi nnif [ -f "requirements.txt" ] || [ -f "pyproject.toml" ]; thenn    echo "   Found Python dependency files"n    echo "   ‚úÖ Language: Python"n    echo "PYTHON"n    exit 0nfi nnif [ -f "Cargo.toml" ]; thenn    echo "   Found Cargo.toml"n    echo "   ‚úÖ Language: Rust"n    echo "RUST"n    exit 0nfi nnif [ -f "go.mod" ]; thenn    echo "   Found go.mod"n    echo "   ‚úÖ Language: Go"n    echo "GO"n    exit 0nfi nnif [ -f "pom.xml" ] || [ -f "build.gradle" ]; thenn    echo "   Found Java build files"n    echo "   ‚úÖ Language: Java"n    echo "JAVA"n    exit 0nfi nnif [ -f "Gemfile" ]; thenn    echo "   Found Gemfile"n    echo "   ‚úÖ Language: Ruby"n    echo "RUBY"n    exit 0nfi nnif [ -f "composer.json" ]; thenn    echo "   Found composer.json"n    echo "   ‚úÖ Language: PHP"n    echo "PHP"n    exit 0nfi nn# Check for source files if no build files foundnif ls *.py >/dev/null 2>&1; thenn    echo "   Found Python source files"n    echo "   ‚úÖ Language: Python"n    echo "PYTHON"n    exit 0nfi nnif ls *.js >/dev/null 2>&1 || ls *.ts >/dev/null 2>&1; thenn    echo "   Found JavaScript/TypeScript source files"n    echo "   ‚úÖ Language: JavaScript/TypeScript"n    echo "JAVASCRIPT"n    exit 0nfi nnif ls *.rs >/dev/null 2>&1; thenn    echo "   Found Rust source files"n    echo "   ‚úÖ Language: Rust"n    echo "RUST"n    exit 0nfi nnif ls *.go >/dev/null 2>&1; thenn    echo "   Found Go source files"n    echo "   ‚úÖ Language: Go"n    echo "GO"n    exit 0nfi nnif ls *.java >/dev/null 2>&1; thenn    echo "   Found Java source files"n    echo "   ‚úÖ Language: Java"n    echo "JAVA"n    exit 0nfi nnif ls *.rb >/dev/null 2>&1; thenn    echo "   Found Ruby source files"n    echo "   ‚úÖ Language: Ruby"n    echo "RUBY"n    exit 0nfi nnif ls *.php >/dev/null 2>&1; thenn    echo "   Found PHP source files"n    echo "   ‚úÖ Language: PHP"n    echo "PHP"n    exit 0nfi nn# Default to common if no specific language detectednecho "   ‚ö†Ô∏è  No specific language detected"necho "   ‚úÖ Using common patterns"necho "COMMON"n