#!/bin/bashn# validate-prp.sh - Validate PRP document against quality standardsnnecho "üîç Validating PRP document..."nn# Check if PRP file is providednif [ -z "$1" ]; thenn    echo "‚ùå ERROR: PRP file required"n    echo "Usage: ./validate-prp.sh <prp-file>"n    exit 1nfinnPRP_FILE="$1"nnecho "   File: $PRP_FILE"nn# Check if file existsnif [ ! -f "$PRP_FILE" ]; thenn    echo "‚ùå ERROR: PRP file not found!"n    exit 1nfinn# Initialize validation scorenSCORE=0nTOTAL_CHECKS=0nISSUES=()nWARNINGS=()nnecho "   üîç Running validation checks..."necho ""nn# Check 1: Required sectionsnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## üéØ Goal" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Goal section present"nelsen    ISSUES+=("Missing Goal section")necho "   ‚ùå Goal section missing"nfinnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## üîç Why This Project" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Why This Project section present"nelsen    ISSUES+=("Missing Why This Project section")necho "   ‚ùå Why This Project section missing"nfinnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## üìã What Will Be Built" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ What Will Be Built section present"nelsen    ISSUES+=("Missing What Will Be Built section")necho "   ‚ùå What Will Be Built section missing"nfinnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## üèóÔ∏è Context & Architecture" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Context & Architecture section present"nelsen    ISSUES+=("Missing Context & Architecture section")necho "   ‚ùå Context & Architecture section missing"nfinnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## üöÄ Implementation Blueprint" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Implementation Blueprint section present"nelsen    ISSUES+=("Missing Implementation Blueprint section")necho "   ‚ùå Implementation Blueprint section missing"nfinnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "## ‚úÖ Validation Gates" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Validation Gates section present"nelsen    ISSUES+=("Missing Validation Gates section")necho "   ‚ùå Validation Gates section missing"nfinn# Check 2: Placeholders replacednTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif ! grep -q "\[.*\]" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ All placeholders replaced"nelsen    PLACEHOLDERS=$(grep -o "\[.*\]" "$PRP_FILE" | head -5)n    ISSUES+=("Placeholders remain: $PLACEHOLDERS")necho "   ‚ùå Placeholders remain in document"nfinn# Check 3: Content qualitynTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif [ $(wc -l < "$PRP_FILE") -gt 50 ]; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Document has substantial content"nelsen    WARNINGS+=("Document seems short - may need more detail")necho "   ‚ö†Ô∏è  Document seems short"nfinn# Check 4: Implementation tasksnTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "Task:" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Implementation tasks defined"nelsen    ISSUES+=("No implementation tasks found")necho "   ‚ùå No implementation tasks found"nfinn# Check 5: Success criterianTOTAL_CHECKS=$((TOTAL_CHECKS + 1))nif grep -q "Success Criteria" "$PRP_FILE"; thenn    SCORE=$((SCORE + 1))necho "   ‚úÖ Success criteria defined"nelsen    ISSUES+=("No success criteria defined")necho "   ‚ùå No success criteria defined"nfinn# Calculate percentagenPERCENTAGE=$((SCORE * 100 / TOTAL_CHECKS))nnecho ""necho "üìä Validation Results:"necho "   Score: $SCORE/$TOTAL_CHECKS ($PERCENTAGE%)"necho ""nn# Display issuesnif [ ${#ISSUES[@]} -gt 0 ]; thenn    echo "‚ùå Issues Found:"n    for issue in "${ISSUES[@]}"; don        echo "   - $issue"n    donen    echo ""nfinnecho ""nn# Display warningsnif [ ${#WARNINGS[@]} -gt 0 ]; thenn    echo "‚ö†Ô∏è  Warnings:"n    for warning in "${WARNINGS[@]}"; don        echo "   - $warning"n    donen    echo ""nfinnecho ""nn# Overall assessmentnif [ $PERCENTAGE -ge 90 ]; thenn    echo "üéâ EXCELLENT: PRP meets high quality standards!"necho "   Ready for implementation."n    exit 0nelif [ $PERCENTAGE -ge 75 ]; thenn    echo "‚úÖ GOOD: PRP is mostly complete."necho "   Address issues before proceeding."n    exit 0nelif [ $PERCENTAGE -ge 60 ]; thenn    echo "‚ö†Ô∏è  FAIR: PRP needs improvement."necho "   Fix issues before implementation."n    exit 1nelse n    echo "‚ùå POOR: PRP needs significant work."necho "   Complete required sections first."n    exit 1nfi