#!/bin/bashn# step-1-read-context.sh - Read and validate initial.mdnnecho "üìñ Step 1: Reading project context..."nnif [ ! -f "initial.md" ]; thenn    echo "‚ùå ERROR: initial.md not found!"n    echo "   Create initial.md with project context first"n    echo "   Use generate-project.sh to create a new project"n    exit 1nfinn# Validate initial.md contentnif ! grep -q "Language:" initial.md; thenn    echo "‚ùå ERROR: initial.md missing Language specification"n    echo "   Add 'Language: [your-language]' to initial.md"n    exit 1nfinnif ! grep -q "Purpose:" initial.md; thenn    echo "‚ùå ERROR: initial.md missing Purpose specification"n    echo "   Add 'Purpose: [your-project-purpose]' to initial.md"n    exit 1nfinn# Extract and display key informationnLANGUAGE=$(grep "Language:" initial.md | cut -d: -f2 | tr -d ' ')nPURPOSE=$(grep "Purpose:" initial.md | cut -d: -f2 | tr -d ' ')nnecho "   ‚úÖ Language: $LANGUAGE"necho "   ‚úÖ Purpose: $PURPOSE"necho "   ‚úÖ Project context validated"nnecho "‚úÖ Step 1: Project context read successfully"n