#!/bin/bashn# step-2-update-template.sh - Update advanced PRP template v3 for project contextnnecho "üîÑ Step 2: Updating advanced PRP template v3..."nn# Detect language from INITIAL.mdnLANGUAGE=$(grep "Language:" INITIAL.md | cut -d: -f2 | tr -d ' ')necho "   Detected language: $LANGUAGE"necho "   Using advanced PRP template v3 (implementation-focused)"nn# Check if language-specific template existsnif [ -f "templates/language-hints/$LANGUAGE.md" ]; thenn    echo "   Found language hints for $LANGUAGE"nelsen    echo "   No specific hints for $LANGUAGE, using common patterns"nfinn# Update advanced template v3n./scripts/update-advanced-prp-template.sh "$LANGUAGE"nnif [ $? -eq 0 ]; thenn    echo "‚úÖ Step 2: Advanced PRP template v3 updated for $LANGUAGE"nelsen    echo "‚ùå ERROR: Failed to update advanced PRP template v3"n    exit 1nfin