#!/bin/bashn# step-2-update-template.sh - Update base PRP template for project contextnnecho "üîÑ Step 2: Updating base PRP template..."nn# Detect language from initial.mdnLANGUAGE=$(grep "Language:" initial.md | cut -d: -f2 | tr -d ' ')necho "   Detected language: $LANGUAGE"nn# Check if language-specific template existsnif [ -f "templates/language-hints/$LANGUAGE.md" ]; thenn    echo "   Found language hints for $LANGUAGE"nelsen    echo "   No specific hints for $LANGUAGE, using common patterns"nfinn# Update base templaten./scripts/update-prp-template.sh "$LANGUAGE"nnif [ $? -eq 0 ]; thenn    echo "‚úÖ Step 2: Base PRP template updated for $LANGUAGE"nelsen    echo "‚ùå ERROR: Failed to update PRP template"n    exit 1nfin