#!/bin/bashn# step-3-create-prp.sh - Create project-specific PRPnnecho "📝 Step 3: Creating project-specific PRP..."nn# Check if updated template existsnif [ ! -f "templates/prp-base-updated.md" ]; thenn    echo "❌ ERROR: Updated PRP template not found!"n    echo "   Step 2 must complete successfully first"n    exit 1nfinn# Get project name from directorynPROJECT_NAME=$(basename "$PWD")necho "   Project: $PROJECT_NAME"nn# Create project-specific PRPn./scripts/create-project-prp.sh "$PROJECT_NAME"nnif [ $? -eq 0 ]; thenn    echo "✅ Step 3: Project-specific PRP created"n    echo "   📄 Generated: $PROJECT_NAME.prp"nelsen    echo "❌ ERROR: Failed to create project PRP"n    exit 1nfin